namespace Olegnax\Athlete2Slideshow\Controller\Adminhtml\Slides;

class Delete extends \Olegnax\Athlete2Slideshow\Controller\Adminhtml\Slides
{
    const ADMIN_RESOURCE = 'Olegnax_Athlete2Slideshow::Slides_Delete';
    /**
     * Delete action
     *
     * @return \Magento\Framework\Controller\ResultInterface
     */
    public function execute()
    {
        /** @var \Magento\Backend\Model\View\Result\Redirect $resultRedirect */
        $resultRedirect = $this->resultRedirectFactory->create();
        // check if we know what should be deleted
        $id = $this->getRequest()->getParam('id');
        if ($id) {
            try {
                // init model and delete
                $model = $this->_objectManager->create(\Olegnax\Athlete2Slideshow\Model\Slides::class);
                $model->load($id);
                $model->delete();
                // display success message
                $this->messageManager->addSuccessMessage(__('You deleted the Slide.'));
                // go to grid
                return $resultRedirect->setPath('*/*/');
            } catch (\Exception $e) {
                // display error message
                $this->messageManager->addErrorMessage($e->getMessage());
                // go back to edit form
                return $resultRedirect->setPath('*/*/edit', ['id' => $id]);
            }
        }
        // display error message
        $this->messageManager->addErrorMessage(__('We can\'t find a Slide to delete.'));
        // go to grid
        return $resultRedirect->setPath('*/*/');
    }
}
