    /**
     * @param string $name
     * @return string
     */
    public function getStyle($name)
    {
        $options = $this->toStyleArray();
        $value = '[]';
        if (array_key_exists($name, $options)) {
            $value = $options[$name];
        }

        return $value;
    }

    /**
     * @return array
     */
    public function toStyleArray()
    {
        if (empty($this->styleArray)) {
            $this->styleArray = [];
            foreach ($this->toOptionArray() as $item) {
                $this->styleArray[$item['value']] = isset($item['style']) ? $item['style'] : '';
            }
        }
        return $this->styleArray;
    }
